mapper.select:
SELECT info.id as "id", info.student_name as "studentName", info.sex as "sex", info.age as "age", info.birth_date as "birthDate", p.user_name as "parentName", school.school_name as "schoolName", organ.organ_name as "organName", info.student_number as "studentNumber", info.major_class as "majorClass", info.class_id as "classId", info.nationality as "nationality", info.id_card as "idCard", info.phone_number as "phoneNumber", info.image as "image", info.family_address_prov as "familyAddressProv", info.family_address_city as "familyAddressCity", info.family_address_area as "familyAddressArea", info.family_address as "familyAddress", info.census_address_prov as "censusAddresspRrov", info.census_address_city as "censusAddresspCity", info.census_address_area as "censusAddresspArea", info.census_address as "censusAddress", info.enter_time as "enterTime", doctor.doctor_name as "doctorName", info.duty_doctor as "dutyDoctor", info.phy_file_date as "phyFileDate", info.create_time as "createTime", info.operator_id as "operatorId" FROM phy_student_info info LEFT JOIN sys_parent p on info.parent_id = p.id LEFT JOIN sys_school school on info.school_id = school.id LEFT JOIN sys_organ organ on info.organ_id = organ.id LEFT JOIN sys_grade_class class on info.class_id = class.id LEFT JOIN sys_doctor doctor on info.doctor_id = doctor.id WHERE 1=1
AND info.id = #{id,jdbcType=VARCHAR}
AND info.student_name = #{studentName,jdbcType=VARCHAR}
AND info.sex = #{sex,jdbcType=TINYINT}
AND info.family_address_prov = #{familyAddressProv,jdbcType=VARCHAR}
AND info.family_address_city = #{familyAddressCity,jdbcType=VARCHAR}
AND info.family_address_area = #{familyAddressArea,jdbcType=VARCHAR}
AND info.family_address = #{familyAddress,jdbcType=VARCHAR}
AND info.census_address_prov = #{censusAddresspRrov,jdbcType=VARCHAR}
AND info.census_address_city = #{censusAddresspCity,jdbcType=VARCHAR}
AND info.census_address_area = #{censusAddresspCity,jdbcType=VARCHAR}
AND info.census_address = #{censusAddress,jdbcType=VARCHAR}
AND info.student_number = #{studentNumber,jdbcType=VARCHAR}
AND info.school_id = #{schoolId,jdbcType=INTEGER}
AND info.class_id = #{classId,jdbcType=INTEGER}

SELECT

FROM
phy_student_info
WHERE 1=1

    AND id = #{id,jdbcType=VARCHAR}


    AND student_name = #{studentName,jdbcType=VARCHAR}


    AND sex = #{sex,jdbcType=TINYINT}


    AND family_address = #{familyAddress,jdbcType=VARCHAR}


    AND census_address = #{censusAddress,jdbcType=VARCHAR}


    AND student_number = #{studentNumber,jdbcType=VARCHAR}


    AND school_id = #{schoolId,jdbcType=INTEGER}


    AND class_id = #{classId,jdbcType=INTEGER}
